// Utilities
function formatDate(date) {
  try {
    return new Intl.DateTimeFormat("ar", {
      weekday: "long", year: "numeric", month: "long", day: "numeric",
    }).format(date);
  } catch (e) { return date.toLocaleDateString("ar"); }
}
function setCookie(name, value, days = 365) {
  const d = new Date(); d.setTime(d.getTime() + days*24*60*60*1000);
  document.cookie = `${name}=${encodeURIComponent(value)};expires=${d.toUTCString()};path=/;SameSite=Lax`;
}
function getCookie(name) {
  const m = document.cookie.match(new RegExp("(?:^|; )" + name.replace(/([.$?*|{}()\[\]\\\/\+^])/g, "\\$1") + "=([^;]*)"));
  return m ? decodeURIComponent(m[1]) : null;
}
function daysBetween(s, e) {
  const S = new Date(s.getFullYear(), s.getMonth(), s.getDate());
  const E = new Date(e.getFullYear(), e.getMonth(), e.getDate());
  return Math.round((E-S)/(1000*60*60*24));
}
function clamp(n,min,max){return Math.max(min,Math.min(max,n));}
function parseDateInput(el) {
  if (el && el.valueAsDate instanceof Date && !isNaN(el.valueAsDate)) return el.valueAsDate;
  const v = el && el.value ? el.value.trim() : "";
  if (!v) return null;
  let m = v.match(/^(\d{4})-(\d{2})-(\d{2})$/); if (m) return new Date(+m[1],+m[2]-1,+m[3]);
  m = v.match(/^(\d{1,2})\/(\d{1,2})\/(\d{4})$/); if (m) return new Date(+m[3],+m[2]-1,+m[1]);
  m = v.match(/^(\d{1,2})-(\d{1,2})-(\d{4})$/); if (m) return new Date(+m[3],+m[1]-1,+m[2]);
  return null;
}
