function startFromInputs(){
  let start=null;
  if(state.mode==='start'){
    if(state.startDateStr){ const m=state.startDateStr.match(/^(\\d{4})-(\\d{2})-(\\d{2})$/); if(m) start=new Date(+m[1],+m[2]-1,+m[3]); }
    if(!start){ const d=parseDateInput(elStartDate); if(d){ start=d; state.startDateStr=ymd(d); save(); if(elStartSaved) elStartSaved.style.display=''; } }
  } else {
    const w=parseInt(state.weekInput,10); if(!isNaN(w)&&w>=1&&w<=40){ const d=new Date(today.getFullYear(),today.getMonth(),today.getDate()); d.setDate(d.getDate()-(w-1)*7); start=d; }
  }
  return start;
}
